import { Component } from '@angular/core';
import { Servicos } from './servicos_agendar';
import { AgendamentoService } from './agendamento.service';
import { Dias } from './dia';
import { Horas } from './hora';
import { Agenda } from './agendar';
import { ActivatedRoute, Router } from '@angular/router';
import { LoginServiceService } from 'src/app/login/login-service.service';
import { cliente } from '../clientes';

import { Barbearia } from './barbearia';



@Component({
  selector: 'app-agendamento',
  templateUrl: './agendamento.component.html',
  styleUrls: ['./agendamento.component.css']
})
export class AgendamentoComponent {

  // IMPORTANDO AS INTERFACE
  servicos: Servicos[] = [];
  dias: Dias[] = [];
  horas: Horas[] = [];
  clientes: cliente[] = []

  barbeariaSelecionada: Barbearia | null = null;
  barbearias: string = "";
  dia: string = "";
  hora: string = "";
  servico: string = "";
  cliente: string = ""

  constructor(
    private agendamentoService: AgendamentoService,
    private router: Router,
    private activatedRoute: ActivatedRoute,
    private loginService: LoginServiceService
  ) {}

  // Exibindo no HTML os documentos
  ngOnInit() {
    this.agendamentoService.getServicos().subscribe(servicos => {
      this.servicos = servicos;
    });

    this.agendamentoService.getDias().subscribe(dias => {
      this.dias = dias;
    });

    this.agendamentoService.getHoras().subscribe(horas => {
      this.horas = horas;
    });

    this.agendamentoService.getCliente().subscribe(cliente => {
      this.clientes = cliente;
      (cliente: { _id: string; }) => {
        console.log(cliente._id);
        LoginServiceService.clienteId = cliente._id;}


    });

    const barbeariaId = this.activatedRoute.snapshot.params['id'];
    if (barbeariaId) {
      this.agendamentoService.getBarbearia(barbeariaId).subscribe(barbearia => {
        this.barbeariaSelecionada = barbearia;
      });
    }
  }

  // Envia o formulário para o MongoDB
  onSubmit(form: any) {
    this.dia = form.value.dia;
    this.hora = form.value.hora;
    this.servico = form.value.servico;
    this.barbearias = form.value.barbearias;

    let agenda: Agenda = {
      barbearias: this.barbearias,
      cliente: LoginServiceService.clienteId,
      dia: this.dia,
      hora: this.hora,
      servicos: this.servico
    };

    this.agendamentoService.newAgendamento(agenda).subscribe(
      response => {
        console.log(response);
        alert('Serviço agendado');
        console.log(response._id);
      },
      error => {
        console.log(error);
        alert('Ocorreu um erro ao agendar');
      }
    );
  }
}
<div class="container">
  <h1>Barber Time</h1>
  <div class="appointment">
    <section class="info" *ngIf="barbeariaSelecionada">
      <div class="barbearia">
        <h1>{{ barbeariaSelecionada.nome }}</h1>
      </div>
    </section>

    <form #f="ngForm" (ngSubmit)="onSubmit(f)">
      <div class="barbearia-info">
        <label for="barbearia">Barbearia:</label>
        <select [(ngModel)]="barbearias" name="barbearias" id="barbearias">
          <option *ngIf="barbeariaSelecionada" [value]="barbeariaSelecionada._id">{{ barbeariaSelecionada.nome }}</option>
        </select>
      </div>

     

      <div class="barbearia-info">
        <label for="servico">clientes:</label>
        <select [(ngModel)]="cliente" name="cliente" id="cliente">
          <option *ngFor="let cliente of clientes" [value]="cliente._id">{{ cliente.nome }}</option>
        </select>
      </div>
      

     



      <div class="barbearia-info">
        <label for="servico">Serviço:</label>
        <select [(ngModel)]="servico" name="servico" id="servico">
          <option *ngFor="let servico of servicos" [value]="servico._id">{{ servico.nome }}</option>
        </select>
      </div>


      <div class="calendar">
        <h4>Selecione o dia e horário:</h4>
        <div class="date">
          <select [(ngModel)]="dia" name="dia" id="dia">
            <option *ngFor="let dia of dias" [value]="dia.dia">{{ dia.dia }}</option>
          </select>
        </div>
        <div class="time">
          <label for="horario">Horário:</label>
          <div class="date">
            <select [(ngModel)]="hora" name="hora" id="hora">
              <option *ngFor="let hora of horas" [value]="hora.hora">{{ hora.hora }}</option>
            </select>
          </div>
        </div>
        <button type="submit" btn-agendar>Finalizar agendamento</button>
      </div>
    </form>
  </div>
</div>
