import { Component } from '@angular/core';
import { MenuService } from './menu.service';
import { Router } from '@angular/router';
import { Barbearia } from './barbearia';
import { LoginServiceService } from 'src/app/login/login-service.service';

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.css']
})
export class MenuComponent {



  barbearia: Barbearia[] = [];

  constructor(private MenuService: MenuService, private  router: Router) {}

  ngOnInit() {
    this.MenuService.getBarbearias().subscribe(barbearias => {
      this.barbearia = barbearias;
      console.log(LoginServiceService.clienteId)
    });
  }

  // mostrarServicos(id: number) {
  //   this.MenuService.getBarbearia(id).subscribe(barbearia => {
  //     // Redirecionar o usuário para a página de serviços da barbearia específica
  //     this.router.navigate(['/servico', barbearia._id]);
  //   });
  // }
}


<header>
  <nav class="navbar navbar-dark bg-dark">
    <div class="container">
      <a class="navbar-brand" href="#">Barber Time</a>
    </div>
  </nav>
</header>

<div class="barbearias-container">
  <div class="barbearia" *ngFor="let barbearia of barbearias">
    <div class="barbearia-info">
      <img src="assets/modelo-de-logotipo-vintage-de-barbearia_441059-26.avif" alt="Barbearia Logo" class="imagem-redimensionada">
      <a [routerLink]="'/servico/' + barbearia._id" class="barbearia-nome">{{ barbearia.nome }}</a>
    </div>
  </div>
</div>
