import { Component } from '@angular/core';
import { Servicos } from './servicos_agendar';
import { AgendamentoService } from './agendamento.service';


@Component({
  selector: 'app-agendamento',
  templateUrl: './agendamento.component.html',
  styleUrls: ['./agendamento.component.css']
})
export class AgendamentoComponent {


  servicos: Servicos[] = [];
  agendamento: Servicos [] = []

  constructor(private AgendamentoService : AgendamentoService ) {}



  nome:string = ""
  email: string = ""
  telefone: string = ""
  data: string = ""
  horario:string = ""


  onSubmit(form: any){
    this.nome = form.value.nome;
    this.email = form.value.email;
    this.telefone = form.value.telefone
    this.data =      form.value.data;
    this.horario = form.value.horario;




    let agendamento: Servicos = {
      nome:this.nome,
      email:this.email,
      telefone:this.telefone,

    }; 

    this.AgendamentoService .newAgendamento(agendamento).subscribe(
      agendamento => {
        console.log(agendamento);

      }
    )
  }

}
