import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { HttpClient,HttpHeaders } from '@angular/common/http';
import{Servicos} from './servicos_agendar'



import { of } from 'rxjs';
import { Router } from '@angular/router';


@Injectable({
  providedIn: 'root'
})
export class AgendamentoService {



  private apiUrl = 'http://localhost:3000//agendamentos'

  constructor(private http: HttpClient) { }

  servicos: Servicos [] = []
  agendamento: Servicos [] = []


  getServicos(): Observable<Servicos[]>{
    return this.http.get<Servicos[]>(this.apiUrl);
  }

  newAgendamento(data:Servicos): Observable<Servicos> {
    return this.http.post<Servicos>(this.apiUrl,data);
  }

}
