import { Component } from '@angular/core';

@Component({
  selector: 'app-servicos',
  templateUrl: './servicos.component.html',
  styleUrls: ['./servicos.component.css']
})
export class ServicosComponent {

}
<!DOCTYPE html>
<html>
<head>
	<title>Perfil da Barbearia</title>
	<style>
		/* Estilo para o cabeçalho */
		header {
			background-color: #333;
			color: white;
			padding: 20px;
			text-align: center;
		}

		/* Estilo para a seção de informações */
		.info {
			display: flex;
			flex-wrap: wrap;
			justify-content: space-around;
			padding: 20px;
		}

		.info div {
			background-color: #f2f2f2;
			border-radius: 10px;
			box-shadow: 0 0 10px #999;
			margin: 10px;
			padding: 20px;
			text-align: center;
			width: 300px;
		}

		.info h2 {
			color: #333;
			font-size: 24px;
			margin-bottom: 10px;
		}

		.info p {
			color: #666;
			font-size: 18px;
			line-height: 1.5;
			margin-bottom: 20px;
		}

		/* Estilo para o rodapé */
		footer {
			background-color: #333;
			color: white;
			padding: 20px;
			text-align: center;
		}
	</style>
</head>
<body>
	<header>
		<h1>Barbearia da Moda</h1>
	</header>

	<section class="info">
		<div>
			<h2>Horário de Funcionamento</h2>
			<p>Segunda a Sexta: 9h às 20h<br>
			Sábado: 9h às 18h<br>
			Domingo: Fechado</p>
		</div>

		<div>
			<h2>Localização</h2>
			<p>Rua das Flores, 123<br>
			Centro - São Paulo/SP<br>
			CEP: 01234-567</p>
		</div>

		<div>
			<h2>Serviços Oferecidos</h2>
			<p>Corte de cabelo masculino<br>
			Barba<br>
			Tratamentos capilares<br>
			Venda de produtos para cabelo e barba</p>
		</div>
	</section>

	<footer>
		<p>Barbearia da Moda - Todos os direitos reservados</p>
	</footer>
</body>
</html>
