import { Component } from '@angular/core';
import { MenuService } from '../menu/menu.service';
import { Barbearia } from '../menu/barbearia';

@Component({
  selector: 'app-servicos',
  templateUrl: './servicos.component.html',
  styleUrls: ['./servicos.component.css']
})
export class ServicosComponent {


  barbearias: Barbearia[] = [];

  constructor(private MenuService: MenuService) {}

  ngOnInit(){
    this.MenuService.getBarbearias().subscribe(
      Barbearias => {
        this.barbearias = Barbearias;
      }
    );

  }
}
<!-- barbearia.component.html -->

	   <header> <h1>barbearia A</h1></header>


<section class="info">
	<div>
		<h2>Horário de Funcionamento</h2>
		<p>Segunda a Sexta: 9h às 20h<br>
		Sábado: 9h às 18h<br>
		Domingo: Fechado</p>
	</div>

	<div>
		<h2>Localização</h2>
		<p>Rua das Flores, 123<br>
		Centro - São Paulo/SP<br>
		CEP: 01234-567</p>
	</div>

	<div>
		<h2>Serviços Oferecidos</h2>
		<p>Corte de cabelo masculino<br>
		Barba<br>
		Tratamentos capilares<br>
		Venda de produtos para cabelo e barba</p>
	</div>


</section>

<button class="btn-agendar" routerLink="/agendamento">Agendar</button>

<footer>
	<p>Barbearia da Moda - Todos os direitos reservados</p>
</footer>
