import { Component } from '@angular/core';
import { Servicos } from './servicos_agendar';
import { AgendamentoService } from './agendamento.service';
import { Dias } from './dia';
import { Horas } from './hora';
import { Agenda } from './agendar';
import { ActivatedRoute, Router } from '@angular/router';

import { Barbearia } from './barbearia';
import { LoginServiceService } from './login-service.service';

@Component({
  selector: 'app-agendamento',
  templateUrl: './agendamento.component.html',
  styleUrls: ['./agendamento.component.css']
})
export class AgendamentoComponent {

  // IMPORTANDO AS INTERFACE
  servicos: Servicos[] = [];
  dias: Dias[] = [];
  horas: Horas[] = [];

  barbeariaSelecionada: Barbearia | null = null;
  barbearias: string = "";
  dia: string = "";
  hora: string = "";
  cliente: string = "";
  servico: string = "";

  constructor(
    private agendamentoService: AgendamentoService,
    private loginService: LoginServiceService, // Adicionado o serviço de login
    private router: Router,
    private activatedRoute: ActivatedRoute
  ) {}

  ngOnInit() {
    // Restante do código ...

    // Obter as informações do cliente logado
    const clienteId = this.loginService.clienteId;
    if (clienteId) {
      this.agendamentoService.getCliente(clienteId).subscribe(cliente => {
        this.cliente = cliente.nome;
      });
    }
  }

  onSubmit(form: any) {
    // Restante do código ...

    let agenda: Agenda = {
      barbearias: this.barbearias,
      cliente: this.cliente,
      dia: this.dia,
      hora: this.hora,
      servicos: this.servico
    };

    // Restante do código ...
  }
}
